package main

import (
	"github.com/pulumi/pulumi/sdk/v2/go/pulumi"
	"github.com/spacelift-io/spacelift-spacelift/sdk/go/spacelift/"
)

func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		_, err := spacelift.NewContext(ctx, "prod_k8s_ie", &spacelift.ContextArgs{
			Description: pulumi.String("Configuration details for the compute cluster in ðŸ‡®ðŸ‡ª"),
			Name:        pulumi.String("Production cluster (Ireland)"),
		})
		if err != nil {
			return err
		}
		return nil
	})
}
```

