    branch: "master",
    repository: "core-infra",
});
const app = new spacelift.Stack("app", {
    branch: "master",
    repository: "app",
});
const test = new spacelift.StackDependency("test", {
    stackId: app.id,
    dependsOnStackId: infra.id,
});
