	pulumi.Run(func(ctx *pulumi.Context) error {
		_, err := spacelift.NewWorkerPool(ctx, "k8s-core", &spacelift.WorkerPoolArgs{
			Csr:         filebase64OrPanic("/path/to/csr"),
			Description: pulumi.String("Used for all type jobs"),
		})
		if err != nil {
			return err
		}
		return nil
	})
}
